import React, { Component } from 'react';


class Clocktick extends Component {
    constructor(props) {
        super(props)

        this.state = {
            counter:0,

        }
    }

    updateCount=()=>{
        this.setState({
            counter:this.state.counter + 1,
        },()=>{
            console.log(this.state.counter);
            return(<React.Fragment>{this.state.counter}</React.Fragment>)})
    }


    render() {
       setInterval(this.updateCount, 5000);
        return (
            <div>
              <h1>{this.state.counter}</h1>
            </div>
        )
    }
}

export default Clocktick;
