import React, { Component } from 'react';


class Clocktick extends Component {
    constructor(props) {
        super(props)

        this.state = {
            counter:0,

        }
    }

    // updateCount=()=>{
    //     this.setState({
    //         counter:this.state.counter + 1,
    //     },()=>{
    //         console.log(this.state.counter);
    //         return(<React.Fragment>{this.state.counter}</React.Fragment>)})
    // }

    componentWillMount() {
        this.setState({
          time: 0,
          timer: setInterval(() => {
            this.setState({ time: this.state.time + 1 })
          }, 1000)
        })
      }
    render() {

        return (
            <div>
              {console.log(this.state.time)}
              {this.state.time}
            </div>
        )
    }
}

export default Clocktick;
