import React, { Component, Fragment } from 'react';
import logo from './logo.svg';
import multiplyByTwo from './logic';
import './App.css';
import Label from './Label';

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      userNumber: 0
    };
    this.inputHandler = this.inputHandler.bind(this);
  }

  inputHandler({ target }) {
    const { value: userNumber } = target;
    this.setState({ userNumber });
  }

  render() {
    const { userNumber } = this.state;
    return (
      <div>
        <input
          onChange={this.inputHandler}
          value={userNumber} />
        <Label valueToDisplay={multiplyByTwo(userNumber)} />
      </div>
    );
  }
}


export default App;
