import React, { Component } from "react";

class Clock extends Component {
  constructor(props) {
    super(props);
    this.state = {};
  }
  componentWillMount() {
    console.log("componentWillMount");
  }
  render() {
    console.log("render");
    return null;
  }
  componentDidMount() {
    console.log("componentDidMount");
  }
}

export default class UpdateClock extends Component{
    constructor(props) {
        super(props)

        this.state = {
             counter:0
        }
    }
        updateCounter(){

            let {counter}=this.state;
            this.setState({
                counter : ++counter
            });
        }
        render(){
            return (<Clock updateCounter={this.updateCounter}></Clock>);
        }
    }
