import React, { Component } from "react";

class Clock extends Component {
  constructor(props) {
    super(props);
    this.state = {
      localCounter: 0
    };
    this.updateLocalCounter = this.updateLocalCounter.bind(this);

  }
  updateLocalCounter() {
    let { localCounter } = this.state;
    this.setState({
      localCounter: ++localCounter
    });
  }
  // Mount Lifecycle events
  UNSAFE_componentWillMount() {
    console.log("componentWillMount");
  }
  componentDidMount() {

    console.log("componentDidMount");
  }
  // Update Lifecycle events
  UNSAFE_componentWillReceiveProps() {
    console.log("componentWillReceiveProps");
  }
  shouldComponentUpdate() {
    console.log("shouldComponentUpdate");
    return true;
  }
  UNSAFE_componentWillUpdate() {
    console.log("componentWillUpdate");
  }
  componentDidUpdate() {
    console.log("componentDidUpdate");
  }
  componentWillUnmount() {
    console.log("componentWillUnmount");
  }
  //   static getDerivedStateFromProps(nextProps, prevState) {}
  render() {
    console.log("render");
    return (
      <div>
        <div>
          <label>Local Counter : {this.state.localCounter}</label>
          <button onClick={this.updateLocalCounter}>Update Local</button>
        </div>
        <div>
          <label>Counter : {this.props.counter}</label>
          <button onClick={this.props.updateCounter}>Update Parent</button>
        </div>
      </div>
    );
  }

}

export default class UpdateClock extends Component {
  constructor(props) {
    super(props);
    this.state = {
      counter: 0
    };
    this.updateCounter = this.updateCounter.bind(this);
  }
  updateCounter() {
    let { counter } = this.state;
    this.setState({
      counter: ++counter
    });
  }
  componentDidMount() {
    this.fetchData(); 
    console.log("componentDidMount");
  }
  render() {
    return this.state.counter % 4 === 0 ? (
      <Clock updateCounter={this.updateCounter} counter={this.state.counter} />
    ) : null;
  }
  fetchData(){

    // fetch('https://randomuser.me/api/?results=50&nat=us,dk,fr,gb')
    fetch('https://rayndomuser.me/api/?results=50&nat=us,dk,fr,gb')
        .then(response => response.json())
        .then(parsedJSON => console.log(parsedJSON.results))
        .catch(error => console.log('parsing failed : here we can insert any error message', error))


  }


}
