import React ,{Component}from 'react';
import logo from './logo.svg';
import './App.css';
import Label from './Label'
import multiplyByTwo from './logic'

class App extends Component{
  constructor(props){
    super(props);
    this.state={
      userNumber:0
    };
    this.inputHandler=this.inputHandler.bind(this);
    }

  inputHandler({ target }) {

    const { value: userNumber } = target;

    this.setState({ userNumber });

  }
  render(){
    return(
      <div>
        <input 
        onChange={this.inputHandler} value={userNumber}></input>
        <Label valueToDisplay={multiplyByTwo(userNumber)} />
      </div>
    )
  }
}

export default App;
